import React, { useState, useContext } from 'react';
import './ProductInfo.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { ProductContext, CartContext } from '../AppProvider';

const ProductInfo = ({ Product }) => {
  const { getStockCount } = useContext(ProductContext);
  const { addToCart } = useContext(CartContext);

  const [selectedQuantity, setSelectedQuantity] = useState(1);
  const count = getStockCount(Product.id);

  const handleQuantityChange = (e) => {
    setSelectedQuantity(parseInt(e.target.value, 10));
  };

  const handleAddToCart = () => {
    if (selectedQuantity > count) {
      alert('Not enough stock available');
      return;
    }
    addToCart(Product, selectedQuantity);
  };

  return (
    <div className="container mt-5">
      <div className="row">
        {/* Left Column: Image Gallery */}
        <div className="col-md-6">
          <div className="image-gallery">
            <img
              src={Product?.image}
              alt={Product?.title || "Product Image"}
              className="img-fluid main-image"
            />
          </div>
        </div>

        {/* Right Column: Product Details */}
        <div className="col-md-6">
          <h1 className="product-info__title">{Product.title}</h1>
          <div className="product-info__rating mb-2">
            {Array(Math.floor(Product.rating.rate))
              .fill()
              .map((_, i) => (
                <span key={i} role="img" aria-label="star">🌟</span>
              ))}
            <span className="text-muted ml-2">({Product.rating.count} ratings)</span>
          </div>
          <div className="product-info__price display-4 text-primary">
            ${Product.price.toFixed(2)}
          </div>
          <p className="text-muted">List Price: <strike>$99.99</strike></p>
          <p className="product-info__description">{Product.description}</p>

          {/* Stock Status */}
          <div className={`availability ${count === 0 ? 'text-danger' : 'text-success'} mb-2`}>
            {count === 0 ? 'Out of Stock' : 'In Stock'}
          </div>

          {/* Quantity Select */}
          {count > 0 && (
            <div className="mb-3">
              <label htmlFor="quantity" className="form-label">Quantity</label>
              <select
                id="quantity"
                value={selectedQuantity}
                onChange={handleQuantityChange}
                className="form-select"
                disabled={count === 0}
              >
                {Array.from({ length: count }, (_, i) => i + 1).map(q => (
                  <option key={q} value={q}>{q}</option>
                ))}
              </select>
            </div>
          )}

          {/* Add to Cart Button */}
          <button
            onClick={handleAddToCart}
            className="btn btn-primary"
            disabled={count === 0}
          >
            Add to Cart
          </button>

          <div className="shipping mt-3">
            <p>Ships from: <strong>ShopSpot</strong></p>
            <p>Sold by: <strong>Kuzil-Direct</strong></p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductInfo;
