import React, { useState, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { UserContext, ProductContext } from '../AppProvider';
import ProductInfo from './ProductInfo';
import SimilarProducts from './SimilarProducts';

const ProductDetails = () => {
  const { user } = useContext(UserContext);
  const { products } = useContext(ProductContext);
  const token = localStorage.getItem('token');
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [count, setCount] = useState(5);

  const api_url = `http://127.0.0.1:8000/categories/Categories/Products/${id}`;

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await fetch(api_url, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Token ${token}`,
          },
        });
        if (!response.ok) {
          throw new Error(`Network response was not ok. Status: ${response.status}`);
        }
        const data = await response.json();
        setProduct(data);
      } catch (error) {
        console.error('Fetch error:', error);
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  const handleBuyNow = (quantity) => {
    if (count >= quantity) {
      setCount(count - quantity);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!product) return <div>No product found</div>;

  const productAmount = products.find(p => p.id === product.id)?.amount || 0;

  return (
    <div className="product-details">
      <ProductInfo Product={product} />

      <SimilarProducts />
    </div>
  );
};

export default ProductDetails;
