import React, { useState } from 'react';
import './Login.css';
import { Link, useNavigate } from "react-router-dom";

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleSignIn = async (e) => {
        e.preventDefault();

        const userData = {
            email: email,
            password: password
        };

        try {
            const response = await fetch('http://127.0.0.1:8000/profiles/login/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',  
                },
                body: JSON.stringify(userData),  
            });

            if (response.ok) {
                const data = await response.json();
                console.log('Login successful', data);
                localStorage.setItem('token', data.token);  
                navigate('/');
            } else {
                const errorData = await response.json();
                console.error('Login failed', errorData);
                alert('Login failed: ' + (errorData.detail || 'Unknown error')); 
            }
        } catch (error) {
            console.error('Error during login:', error);
            alert('An error occurred: ' + error.message);  // Handle network errors
        }
    };

    return (
        <div className='login'>
            <Link to='/'>
                <img
                    className="login__logo"
                    src="/images/logo.png"
                    alt="logo"
                />
            </Link>

            <div className='login__container'>
                <h1>Sign-in</h1>

                <form onSubmit={handleSignIn}>
                    <h5>E-mail</h5>
                    <input
                        type='text'
                        value={email}
                        onChange={e => setEmail(e.target.value)}
                        style={{ border: '2px solid #000' }}
                    />

                    <h5>Password</h5>
                    <input
                        type='password'
                        value={password}
                        onChange={e => setPassword(e.target.value)}
                        style={{ border: '2px solid #000' }}
                    />

                    <button type='submit' className='login__signInButton'>Sign In</button>
                </form>
                <Link className='login__registerButton' to='/login/RegistrationForm'>
                    Create your ShopSpot Account
                </Link>
            </div>
        </div>
    );
}

export default Login;
