import React from "react";
import "./Product.css";
import { Link } from "react-router-dom";

function Product(props) {
    const { Product } = props;

    return (
        <div className="product__card">
            {/* Product Image */}
            <img className="product__image" src={Product?.image} alt={Product?.name || "Product Image"} />

            {/* Product Details */}
            <div className="product__details">
                <h2 className="product__name">{Product?.name}</h2>
                <p className="product__description">{Product?.description}</p>

                <p className="product__price">
                    <strong>Price: </strong>
                    <span>${Product?.price}</span>
                </p>
                
                <p className="product__quantity">
                    <strong>Quantity Available: </strong>
                    <span>{Product?.quantity}</span>
                </p>

                <p className="product__color">
                    <strong>Color: </strong>
                    <span>{Product?.color}</span>
                </p>

                <p className="product__size">
                    <strong>Size: </strong>
                    <span>{Product?.size}</span>
                </p>

                {/* View Details Button */}
                <Link className="product__button" to={`/productDetails/${Product?.id}`}>
                    View Details
                </Link>
            </div>
        </div>
    );
}

export default Product;
