// AppProvider.js
import React, { createContext, useState, useEffect } from 'react';

// UserContext for authentication
export const UserContext = createContext(null);

// CartContext for shopping cart and stock management
export const CartContext = createContext(null);

// ProductContext for product data
export const ProductContext = createContext(null);

export const AppProvider = ({ children }) => {
    // State for UserContext
    const [user, setUser] = useState(null);

    // State for CartContext
    const [cart, setCart] = useState([]);
    const [products, setProducts] = useState([]);

    // Load user data from localStorage on mount
    useEffect(() => {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            setUser(JSON.parse(storedUser));
        }
    }, []);

    // Fetch products from API and set state
    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await fetch('https://fakestoreapi.com/products');
                const data = await response.json();
                setProducts(data);
                const modifiedData = data.map(product => ({
                    ...product,
                    amount: 20 
                }));
                setProducts(modifiedData);
                
            } catch (error) {
                console.error('Failed to fetch products:', error);
            }
        };

        fetchProducts();
    }, []);
    // Login function to update user context and store in localStorage
    const loginUser = (userData) => {
        setUser(userData);
        localStorage.setItem('user', JSON.stringify(userData));
    };

    // Logout function to clear user context and localStorage
    const logoutUser = () => {
        setUser(null);
        localStorage.removeItem('user');
    };

    // Add to Cart function
    const addToCart = (product, quantity) => {
        const existingProduct = cart.find(item => item.product.id === product.id);
        if (existingProduct) {
            setCart(cart.map(item => item.product.id === product.id ?
                { ...item, quantity: item.quantity + quantity } : item));
        } else {
            setCart([...cart, { product, quantity }]);
        }

        // Update stock count
        setProducts(products.map(p => p.id === product.id ?
            { ...p, stock: p.stock - quantity } : p));
    };

    // Remove from Cart function
    const removeFromCart = (productId) => {
        const removedProduct = cart.find(item => item.product.id === productId);
        setCart(cart.filter(item => item.product.id !== productId));

        // Restore stock count
        setProducts(products.map(p => p.id === productId ?
            { ...p, stock: p.stock + (removedProduct ? removedProduct.quantity : 0) } : p));
    };

    // Get stock count for a product
    const getStockCount = (productId) => {
        const product = products.find(p => p.id === productId);
        return product ? product.amount : 0;
    };

    return (
        <UserContext.Provider value={{ user, loginUser, logoutUser }}>
            <ProductContext.Provider value={{ products }}>
                <CartContext.Provider value={{ cart, addToCart, removeFromCart, getStockCount }}>
                    {children}
                </CartContext.Provider>
            </ProductContext.Provider>
        </UserContext.Provider>
    );
};
