import React from "react";
import "./Product.css";
import { Link } from "react-router-dom";
import Button from 'react-bootstrap/Button';

function Product(props) {
  const { Product } = props; // Extract the Product object from props

  return (
    <div className="product">
      <div className="product__info">
        <p className="product__title">{Product.title}</p>
        <p className="product__price">
          <small>$</small>
          <strong>{Product.price}</strong>
        </p>
        <div className="product__rating">
          {Array(Math.floor(Product.rating.rate)).fill().map((_, i) => (
            <span key={i} role="img" aria-label="star">ðŸŒŸ</span>
          ))}
        </div>
      </div>

      <img src={Product.image} alt={Product.title} />

      <Link className="product__button" to={`/productDetails/${Product.id}`}>
        <Button variant="primary">Details</Button>
      </Link>
    </div>
  );
}

export default Product;
