import React, { createContext, useState, useEffect } from 'react';

export const UserContext = createContext(null);
export const CartContext = createContext(null);
export const ProductContext = createContext(null);

export const AppProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [cart, setCart] = useState([]);
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            setUser(JSON.parse(storedUser));
        }
    }, []);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await fetch('https://fakestoreapi.com/products');
                const data = await response.json();
                const modifiedData = data.map(product => ({
                    ...product,
                    amount: 20
                }));
                setProducts(modifiedData);
            } catch (error) {
                console.error('Failed to fetch products:', error);
            }
        };
        fetchProducts();
    }, []);

    const fetchCartData = async () => {
        const token = localStorage.getItem('token');
        try {
            const response = await fetch('http://127.0.0.1:8000/categories/Categories/cart/', {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                }
            });
            if (response.ok) {
                const cartData = await response.json();
                setCart(cartData);
            } else {
                console.error('Failed to fetch cart data');
            }
        } catch (error) {
            console.error('Error fetching cart data:', error);
        }
    };

    const updateCartDataOnBackend = async (cart) => {
        const token = localStorage.getItem('token');
        try {
            const response = await fetch('http://127.0.0.1:8000/categories/Categories/cart/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                },
                body: JSON.stringify(cart),
            });
            if (!response.ok) {
                console.error('Failed to update cart data');
            }
        } catch (error) {
            console.error('Error updating cart data:', error);
        }
    };

    const addToCart = (product, quantity) => {
        const existingProduct = cart.find(item => item.product.id === product.id);
        let updatedCart;
        if (existingProduct) {
            updatedCart = cart.map(item => item.product.id === product.id ?
                { ...item, quantity: item.quantity + quantity } : item);
        } else {
            updatedCart = [...cart, { product, quantity }];
        }
        setCart(updatedCart);

        updateCartDataOnBackend(updatedCart);
    };

    const removeFromCart = (productId) => {
        const updatedCart = cart.filter(item => item.product.id !== productId);
        setCart(updatedCart);
        updateCartDataOnBackend(updatedCart);
    };

    return (
        <UserContext.Provider value={{ user, setUser }}>
            <ProductContext.Provider value={{ products, getStockCount }}>
                <CartContext.Provider value={{ cart, addToCart, removeFromCart, fetchCartData }}>
                    {children}
                </CartContext.Provider>
            </ProductContext.Provider>
        </UserContext.Provider>
    );
};
