// ProductActions.js
import React, { useState } from 'react';
import './ProductActions.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const ProductActions = ({ Product,count, }) => {
  const [quantity, setQuantity] = useState(1);

  const handleAddToCart = () => {
    console.log(`Added ${quantity} of ${Product.title} to cart.`);
    // Here you can add the logic to actually add the product to the cart
  };

  const handleBuyNow = () => {
    console.log(`Bought ${quantity} of ${Product.title}.`);
    // Here you can add the logic to proceed with the purchase
  };

  return (
    <div className="product-actions mt-4">
      <div className="quantity-selector mb-3">
        <label htmlFor="quantity" className="mr-2">Quantity:</label>
        <select
          id="quantity"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          className="form-control"
          style={{ width: '80px', display: 'inline-block' }}
        >
          {[...Array(10).keys()].map((x) => (
            <option key={x + 1} value={x + 1}>
              {x + 1}
            </option>
          ))}
        </select>
      </div>
      <button className="btn btn-warning btn-lg btn-block" onClick={handleAddToCart}>
        Add to Cart
      </button>
      <button className="btn btn-secondary btn-lg btn-block mt-2" onClick={handleBuyNow}>
        Buy Now
      </button>
    </div>
  );
};

export default ProductActions;
