import React from "react";
import "./Product.css";
import { Link } from "react-router-dom";
import  { useContext } from "react";    
import { ProductContext } from "../AppProvider";
function Product(props) {
    const { products } = useContext(ProductContext);
    const prod = products.find(p => p.id ===products.id );
    console.log(prod)
    return (
        <div className="product">
            <div className="product__info">
                {Product?.title && <p className="product__title">{prod.title}</p>}
                <p className="product__price">
                    <small>$</small>
                    <strong>{Product?.price}</strong>
                </p>
                <div className="product__rating">
                    {Array(Math.floor(Product?.rating?.rate || 0)).fill().map((_, i) => (
                        <span key={i} role="img" aria-label="star">ðŸŒŸ</span>
                    ))}
                </div>
            </div>

            <img src={Product?.image} alt={Product?.title || "Product Image"} />

            <Link className="product__button" to={`/productDetails/${Product?.id}`}>
                View Details
            </Link>
        </div>
    );
}

export default Product;
