import React, { useState, useContext } from "react";
import { ProductContext } from "./AppProvider";
import Product from "./ProductListng/ProductCard";
const SearchResults = () => {
  const { products } = useContext(ProductContext); 
  const [searchQuery, setSearchQuery] = useState(""); 

  const search_params = ["name", "description", "category"];

  const search = (products) => {
    return products.filter((product) =>
      search_params.some((param) =>
        product[param]?.toString().toLowerCase().includes(searchQuery.toLowerCase())
      )
    );
  };

  const filteredProducts = search(products);
console.log(filteredProducts)
  return (
    <>
      <input
        type="text"
        placeholder="Search products..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)} 
      />
      <h1>Search Results</h1>
      <div className="product-list">
        {filteredProducts.length > 0 ? (
          filteredProducts.map((product) => (
           <Product product={product} key={product.id} />  
          ))
        ) : (
          <p>No products found.</p>
        )}
      </div>
    </>
  );
};

export default SearchResults;
