import "./Product.css";
import { Link } from "react-router-dom";

function Product(pro) {
 const { Product } = pro;
console.log(props)
  return (
    <>
    <div className="product">
      <div className="product__info">
        
      {Product?.title && <p className="product__title">{Product.title}</p>}
      <p className="product__price">
          <small>$</small>
          <strong>{Product.price}</strong>
          {Product?.title && <p className="product__title">{Product.title}</p>}
        </p>
        <div className="product__rating">
          {Array(Math.floor(Product.rating.rate)).fill().map((_, i) => (
            <span key={i} role="img" aria-label="star">ðŸŒŸ</span>
          ))}
        </div>
      </div>

      <img src={Product.image} alt="oops" />

      <Link className="product__button" to={`/productDetails/${Product.id}`}>
      </Link>
    </div>
    </>
    
  );
}

export default Product;
