import React from 'react';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import Home from './Home';
import './App.css';
import Header from './Header/Header';
import Login from './login/Login';
import Slider from './ProductListng/Slider';
import ProductDetails from './ProductDetails/ProductDetails';
import { CartProvider } from './Cart/CartContext';
import Cart from './Cart/Cart';
import Checkout from './CheckOut/Checkout';
import RegistrationForm from './login/RegistrationForm';

function App() {
  return (
    <CartProvider>
      <Router>
        <AppRoutes />
      </Router>
    </CartProvider>
  );
}

function AppRoutes() {
  const location = useLocation();
  // List all routes where you don't want to show the Header
  const routesWithoutHeader = ['/login', '/login/RegistrationForm'];
  const showHeader = !routesWithoutHeader.includes(location.pathname);

  return (
    <div className="app">
      {showHeader && <Header />}
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/login/RegistrationForm" element={<RegistrationForm />} />
        <Route path="/cart" element={<Cart />} />
        <Route path="/productDetails/:id" element={<ProductDetails />} />
        <Route path="/checkout" element={<Checkout />} />
        <Route
          path="/"
          element={
            <>
              <Slider />
              <Home />
            </>
          }
        />
      </Routes>
    </div>
  );
}

export default App;
