import React from 'react';
import './ProductInfo.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const ProductInfo = (props) => {
  const { Product } = props;
  return (
    <div className="container mt-5">
      <div className="row">
        <div className="col-md-6">
          <div className="product-image text-center">
            <img
              src={Product?.image}
              alt={Product?.title || "Product Image"}
              className="img-fluid"
            />
          </div>
        </div>
        <div className="col-md-6">
          <div className="product-details">
            <h1 className="product-info__title">{Product.title}</h1>
            <p className="product-info__price display-4 text-primary">
              ${Product.price.toFixed(2)}
            </p>
            <div className="product-info__rating mb-3">
              {Array(Math.floor(Product.rating.rate))
                .fill()
                .map((_, i) => (
                  <span key={i} role="img" aria-label="star">ðŸŒŸ</span>
                ))}
              <small className="text-muted"> ({Product.rating.count} ratings)</small>
            </div>
            <p className="product-info__description lead">{Product.description}</p>
            <button className="btn btn-warning btn-lg btn-block mt-4">
              Add to Cart
            </button>
            <button className="btn btn-secondary btn-lg btn-block mt-2">
              Buy Now
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductInfo;
