import React, { useState, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { UserContext } from '../AppProvider';
import SimilarProducts from './SimilarProducts';

const ProductDetails = () => {
  const { user } = useContext(UserContext);
  const token = localStorage.getItem('token');
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [count, setCount] = useState(5);

  const api_url = `http://127.0.0.1:8000/categories/Categories/Products/${id}`;

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await fetch(api_url, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Token ${token}`,
          },
        });
        if (!response.ok) {
          throw new Error(`Network response was not ok. Status: ${response.status}`);
        }
        const data = await response.json();
        setProduct(data);
      } catch (error) {
        console.error('Fetch error:', error);
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  const handleBuyNow = (quantity) => {
    if (count >= quantity) {
      setCount(count - quantity);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!product) return <div>No product found</div>;

  return (
    <div className="product-details">
      <div className="product-details__info">
        <h2>Product Details</h2>
        <div className="product-details__image">
          <img src={product.image} alt={product.name} />
        </div>
        <div className="product-details__content">
          <p><strong>ID:</strong> {product.id}</p>
          <p><strong>Name:</strong> {product.name}</p>
          <p><strong>Description:</strong> {product.description}</p>
          <p><strong>Price:</strong> ${product.price}</p>
          <p><strong>Quantity:</strong> {product.quantity}</p>
          <p><strong>Color:</strong> {product.color}</p>
          <p><strong>Size:</strong> {product.size}</p>
          <p><strong>Category ID:</strong> {product.category}</p>
          <p><strong>Seller ID:</strong> {product.seller}</p>
        </div>
        <div className="product-details__actions">
        
          <ProductActions product={product} onBuyNow={handleBuyNow} />
        </div>
      </div>
      <SimilarProducts />
    </div>
  );
};

export default ProductDetails;
