import React from 'react';
import './ProductInfo.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const ProductInfo = ({ Product,count }) => {
  return (
    <div className="container mt-5">
      <div className="row">
        {/* Left Column: Image Gallery */}
        <div className="col-md-6">
          <div className="image-gallery">
            <img
              src={Product?.image}
              alt={Product?.title || "Product Image"}
              className="img-fluid main-image"
            />
            {/* Thumbnail images */}
            <div className="thumbnails d-flex mt-3">
              <img src={Product?.image} alt="thumbnail 1" className="img-thumbnail" />
              {/* Additional thumbnails can be added here */}
            </div>
          </div>
        </div>

        {/* Right Column: Product Details */}
        <div className="col-md-6">
          <h1 className="product-info__title">{Product.title}</h1>
          <div className="product-info__rating mb-2">
            {Array(Math.floor(Product.rating.rate))
              .fill()
              .map((_, i) => (
                <span key={i} role="img" aria-label="star">ðŸŒŸ</span>
              ))}
            <span className="text-muted ml-2">({Product.rating.count} ratings)</span>
          </div>
          <div className="product-info__price display-4 text-primary">
            ${Product.price.toFixed(2)}
          </div>
          <p className="text-muted">List Price: <strike>$99.99</strike></p>

          <div className="availability text-success mb-2">{count ? "In Stock":"Out Of Stock"}</div>

          <p className="product-info__description">{Product.description}</p>
          <div className="product-actions mt-4">
            <button className="btn btn-warning btn-lg btn-block">Add to Cart</button>
            <button className="btn btn-secondary btn-lg btn-block mt-2" onClick={()=>count--}>Buy Now</button>
          </div>

          <div className="shipping mt-3">
            <p>Ships from: <strong>Amazon</strong></p>
            <p>Sold by: <strong>Kuzil-Direct</strong></p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductInfo;
